import React, { useState, useEffect } from "react";
import Axios from "axios";
import parse from "html-react-parser";
import Mountain from './../../assets/about_mountain.jpg';
import CircularProgress from '@material-ui/core/CircularProgress';
import Backdrop from '@material-ui/core/Backdrop';


// http://alberto.codes/wp/wp-json/wp/v2/posts/15

export default function App() {
    const [post, setPost] = useState(null);
    const [loading, setLoading] = useState(false);


    const wordpressFunction = async () => {
        try {
            const data = await Axios.get('http://alberto.codes/wp/wp-json/wp/v2/posts/15')
                .then(res => {
                    setPost(res.data);
                })
            setLoading(true)
        } catch (err) {
            console.log(err)
        }
    }

    // useEffect(() => {
    //     Axios.get("http://alberto.codes/wp/wp-json/wp/v2/posts/15")
    //         .then(res => {
    //             setPost(res.data);
    //         })
    //     setLoading(true);

    // }, [setPost]);


    useEffect(() => {
        wordpressFunction();
    }, [setPost]);

    return (
        <div className="aboutme">
            <div className="aboutme_post">

                <h1>{post}</h1>
                {loading ? (post) : (<CircularProgress />)}

                {/* {post && (
                    loading ? (<div className="aboutme__content">
                        <div className="aboutme__content_image">
                            <img src={Mountain} alt="about me" />
                        </div>
                        <div className="aboutme__content_text">
                            <h1>{post.title.rendered}</h1>
                            <div>{parse(post.content.rendered)}</div>

                        </div>
                    </div>) : ("")

                )} */}

            </div>
        </div>
    );
}


